import {useState} from 'react';
import Layout from '../components/MyLayout.js';
const axios = require('axios');


const NewPost = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  
  const handleTitle = (e) => {
    setTitle(e.target.value);
  }

  const handleBody = (e) => {
    setBody(e.target.value);
  }

  const handleSubmit = () => {
    const newPost = {
      title,
      body,
    }
    axios.post('https://simple-blog-api.crew.red/posts', {newPost}).then(resp => console.log(resp)).catch(error => console.log(error));
  }

  return (
    <Layout>
      <h2>Create your post</h2>
      <p>{title}</p>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="title" onChange={handleTitle}/> <br/>
        <textarea placeholder="post" onChange={handleBody} /> <br/>
        <button type="submit">Create</button>
      </form>      
    </Layout>
  )
}

export default NewPost;
