import { createStore, applyMiddleware } from 'redux';
import nextConnectRedux from 'next-connect-redux'
import ReduxThunk from 'redux-thunk';
const axios = require('axios');

export const actionTypes = {
  FETCH_POSTS_SUCCESS: 'FETCH_POSTS_SUCCESS',
};

export const fetchPostsSuccess = posts => ({
  type: actionTypes.FETCH_POSTS_SUCCESS,
  posts,
});

export const fetchPosts = () => dispatch => {
  axios.get('https://simple-blog-api.crew.red/posts').then(response => {
    dispatch(fetchPostsSuccess(response.data))
  });
};

const getInitial = () => {
  const request = axios.get('https://simple-blog-api.crew.red/posts');
  const posts = request.data;

  return posts;
}

const initialPosts = {
  posts: [],
};

const reducer = (state = initialPosts, action) => {
  switch (action.type) {
    case actionTypes.FETCH_POSTS_SUCCESS:
      return {
        posts: action.posts,
      };
    case actionTypes.FETCH_POSTS_ERROR:
      return {
        error: action.error,
      };
    default:
      return state;
  }
};

export const store = () => {
  return createStore(reducer, initialPosts, applyMiddleware(ReduxThunk));
}

export const nextConnect = nextConnectRedux(store);
