import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Layout from '../../components/MyLayout';
import axios from 'axios';
import styled from 'styled-components'

const Button = styled.button`
  width: 200px;
  height: 60px;
  font-size: 18px;
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
  cursor: pointer;
`;

const Input = styled.input`
  width: 300px;
  height: 40px;
  padding: 0 10px;
  margin-left: 2em;
`

const PostsHeader = styled.h2`  
  margin: 0 1em;
  padding: 1.5em;
  font-weight: bold;
  font-size: 30px;  
`;

const ListItem = styled.li` 
  width: 50%; 
  padding: 1em 1.5em; 
  margin: 0.4em;
  background-color: #F8E0EC;
  list-style: none;
  border-radius: 7px;
`;

const BodyContent = styled.div` 
  margin-left: 2em;
  padding: 2em 3em;
  width: 80%; 
  font-size: 18px;
  background-color: #eee;
  border-radius: 10px;
`;

const CommentsHeader = styled.div` 
  margin-left: 2em;
  padding: 2em 3em;
  width: 80%; 
  font-size: 18px;
  font-weight: bold;
`;

const Post = props => {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('https://simple-blog-api.crew.red/comments',
      { body: value, postId: props.post.id });
    setValue('');
  }

  return (
    <Layout>
      <PostsHeader>{props.post.title}</PostsHeader>
      <BodyContent>{props.post.body}</BodyContent>
      {props.comments && (
        <>
          <CommentsHeader>Comments:</CommentsHeader>
          <ul>
            {props.comments.map(comment => 
              <ListItem key={comment.id}>
                {comment.body}
              </ListItem>)}
          </ul>
        </>)}
        <form onSubmit={handleSubmit}>
            <Input type="text" 
                    placeholder="comment"
                    value={value}
                    onChange={handleChange} />
            <Button type="submit">Add comment</Button>
        </form>
    </Layout>
  )
}

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  const getPost = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);
  const getComments = await axios.get('https://simple-blog-api.crew.red/comments');
  
  const post = getPost.data;
  const currentComments = getComments.data.filter(comment => comment.postId === Number(id));
  return { post, comments: currentComments };
};

Post.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number.isRequired,
    title: PropTypes.string.isRequired,
    body: PropTypes.string.isRequired,
  }),
  comments: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      postId: PropTypes.number,
      body: PropTypes.string,
  })).isRequired,
};

export default Post;
