import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Layout from '../../components/MyLayout';
import axios from 'axios';
import styled from 'styled-components'

const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
  cursor: pointer;
`;

const Post = props => {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('https://simple-blog-api.crew.red/comments',
      { body: value, postId: props.post.id });
    setValue('');
  }

  return (
    <Layout>
      <h2>{props.post.title}</h2>
      <p>{props.post.body}</p>
      {props.comments && (
        <ul>
          {props.comments.map(comment => 
            <li key={comment.id}>
              {comment.body}
            </li>)}
        </ul>)}
        <form onSubmit={handleSubmit}>
            <input type="text" 
                    placeholder="comment"
                    value={value}
                    onChange={handleChange} />
            <Button type="submit">Add comment</Button>
        </form>
    </Layout>
  )
}

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  const getPost = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);
  const getComments = await axios.get('https://simple-blog-api.crew.red/comments');
  
  const post = getPost.data;
  console.log(getComments.data);
  const currentComments = getComments.data.filter(comment => comment.postId === Number(id));
  return { post, comments: currentComments };
};

Post.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number.isRequired,
    title: PropTypes.string.isRequired,
    body: PropTypes.string.isRequired,
  }),
  comments: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      postId: PropTypes.number,
      body: PropTypes.string,
  })).isRequired,
};

export default Post;
