import React from 'react';
import PropTypes from 'prop-types';
import Layout from '../../components/MyLayout';
import axios from 'axios';
import {store, nextConnect, fetchPostsWithId} from '../../redux/store';

const Post = props => {
  console.log(props);
  return
  (
    <Layout>
      {/* <h1>{props.post.title}</h1>
      <p>{props.post.body}</p> */}
    </Layout>
  )
}

Post.getInitialProps = async function(context) {
  // console.log(store);
  const { id } = context.query;
  // await store().dispatch(fetchPostsWithId(id));
  // return {title: 'ss', body: 'dd'};
  const res = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);
  const post = res.data;

  return { post };
};

Post.propTypes = {
  post: PropTypes.shape({
    title: PropTypes.string.isRequired,
    body: PropTypes.string.isRequired,
  }),
};

export default nextConnect((state) => state)(Post)
