import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Layout from '../../components/MyLayout';
import axios from 'axios';

const Post = props => {
  const [value, setValue] = useState('');
  return (
    <Layout>
      <h1>{props.post.title}</h1>
      <p>{props.post.body}</p>
      {props.comments && (
        <ul>
          {props.comments.map(comment => 
            <li key={comment.id}>
              {comment.body}
            </li>)}
        </ul>)}
        <form>
          <label>
            <input type="text" placeholder='comment'  />
          </label>
        </form>
    </Layout>
  )
}

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  const getPost = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);
  const getComments = await axios.get('https://simple-blog-api.crew.red/comments');
  const post = getPost.data;
  const currentComments = getComments.data.filter(comment => comment.postId === id);

  return { post, comments: currentComments };
};

Post.propTypes = {
  post: PropTypes.shape({
    title: PropTypes.string.isRequired,
    body: PropTypes.string.isRequired,
  }),
  comments: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      body: PropTypes.string,
  })).isRequired,
};

export default Post;
