import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import Layout from '../components/MyLayout';
import Link from 'next/link';
import { fetchPosts } from '../redux/store';

// const axios = require('axios');

const Index = props => (
  <Layout>
    <h2>Latest posts</h2>
    <ul>
      {props.posts.map(post => (
        <li key={post.id}>
          <Link href="/posts/[id]" as={`/posts/${post.id}`}>
            <a>{post.title}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
);

Index.getInitialProps = function({ reduxStore, req }) {
  console.log(reduxStore);
  const isServer = !!req;
  reduxStore.dispatch(fetchPosts(isServer));

  return {};
  // const res = axios.get('https://simple-blog-api.crew.red/posts');
  // const data = res.data;
  // console.log(data);
  // return {
  //   posts: [...data],
  // };
};

export default Index;
